import React, { Component } from 'react';
import './App.css';
import UserOutput from './UserOutput';
import UserInput from './UserInput';

class App extends Component {

  state = {
    username: "whatever",
    password: "secret"
  }

  updateUsernameHandler = (target) => {
    let state = {
      username: "JAMES"
    }

    this.setState(state)

    console.log(this.state)
  }

  render() {
    return (
      <div className="App">
        <UserInput inputChangedHandler={this.updateUsernameHandler}/>
        <UserOutput username={this.state.username}/>
        <UserOutput username={this.state.username}/>
        <UserOutput username={this.state.username}/>
      </div>
    );
  }
}

export default App;
